StartTest(function(t) {
    
    var async0 = t.beginAsync()
    
    use([ 'Hash', 'Encode', 'Hash.MD5', 'Hash.SHA256', 'Hash.SHA1', 'Hash.SHA256', 'Hash.SHA512', 'Hash.RIPEMD160' ], function () {
        
        //======================================================================================================================================================================================================================================================
        t.diag('Sanity')
        
        t.ok(Hash && Encode && Hash.MD5 && Hash.SHA1 && Hash.SHA256 && Hash.SHA512 && Hash.RIPEMD160, "Hash and supporting scripts are all here")

        t.endAsync(async0)
        
        t.done()
    })
})

