StartTest(function(t) {
    
    var async0 = t.beginAsync()
    
    use([ 'Hash', 'joosed-samples-SHA1' ], function () {
        
        //======================================================================================================================================================================================================================================================
        t.diag('Check ASCII strings against known good SHA-1 hash table')

        // var sys = require('sys')

        t.ok(Hash.SHA1.sha1_vm_test(), 'the built-in test for SHA-1 passed')

        var known_good_sha1_hashes_ascii = Samples_SHA1.getStringsANDhashes_ASCII()
 
        var compute_and_compare_hashes = function (hash_table) {
          var result = true
          for (var i = 0 ; i < hash_table.length ; i++) {
            if (Hash.sha1(hash_table[i][0]) != hash_table[i][1]) {
              // sys.puts(Hash.sha1(hash_table[i][0]) + '\t' + hash_table[i][0] + '\t' + hash_table[i][1])
              result = false
            }
          }
          return result
        }

        t.ok(compute_and_compare_hashes(known_good_sha1_hashes_ascii), "Computed and known good hashes for ASCII strings all match!")

        //======================================================================================================================================================================================================================================================

        t.diag('Check UTF-8 strings against known good SHA-1 hash table')

        var known_good_sha1_hashes_utf8 = Samples_SHA1.getStringsANDhashes_UTF8()
 
        var compute_and_compare_hashes = function (hash_table) {
          var result = true
          for (var i = 0 ; i < hash_table.length ; i++) {
            if (Hash.sha1(hash_table[i][0]) != hash_table[i][1]) {
              // sys.puts(Hash.sha1(hash_table[i][0]) + '\t' + hash_table[i][0] + '\t' + hash_table[i][1])
              result = false
            }
          }
          return result
        }

        t.ok(compute_and_compare_hashes(known_good_sha1_hashes_utf8), "Computed and known good hashes for UTF-8 strings all match!")

        t.endAsync(async0)
        
        t.done()
    })
})    

